import React from "react";
import { useFormik } from "formik";
import * as Yup from "yup"

export default function Signup() {

    const formik = useFormik({
        initialValues: {
            Mname: "",
            Mnumber: "",
            Memail: "",
            Mtext: ""
        },
        validationSchema: Yup.object({
            Mname: Yup.string().max(17, "Ваше имя не должно превышать 17 символов").required("Required"),
            
        }),
        onSubmit: (values) => {
            console.log(values);
        }
    })
    // console.log(formik.values);
    return (
        <form onSubmit={formik.handleSubmit}>
            <div className="Mform_input">
                <input
                    id="Mname"
                    name="Mname"
                    type="text"
                    placeholder="Ваше имя"
                    onChange={formik.handleChange}
                    value={formik.values.Mname}
                />
            </div>
            <div className="Mform_input">
                <input
                    id="Mnumber"
                    name="Mnumber"
                    type="number"
                    placeholder="Телефон"
                    onChange={formik.handleChange}
                    value={formik.values.Mnumber}
                />
            </div>
            <div className="Mform_input">
                <input
                    id="Memail"
                    name="Memail"
                    type="email"
                    placeholder="E-mail"
                    onChange={formik.handleChange}
                    value={formik.values.Memail}
                />
            </div>
            <div className="Mform_textarea">
                <textarea
                    id="Mtext"
                    name="Mtext"
                    type="text"
                    placeholder="Ваш комментарий"
                    onChange={formik.handleChange}
                    value={formik.values.Mtext}
                ></textarea>
            </div>
            <button type="submit" className="Mform_button" >СВЯЖИТЕСЬ С НАМИ</button>
        </form>
    )
}
